import { useState } from 'react';
import { createUserWithEmailAndPassword, onAuthStateChanged, signOut, signInWithEmailAndPassword, getAuth } from 'firebase/auth';
import { auth } from './firebase-config';
import './styles.css'

function App() {

  const [email, setEmail] = useState('');
  const [pssw, setPssw] = useState('');
  const [user, setUser] = useState({});

  var mysql = require('mysql');

  var con = mysql.createConnection({
    host: "localhost",
    user: "yourusername",
    password: "yourpassword",
    database: "mydb"
  });

  con.connect(function(err) {
    if (err) throw err;
    console.log("Connected!");
    var sql = "INSERT INTO customers (name, address) VALUES ('Company Inc', 'Highway 37')";
    con.query(sql, function (err, result) {
      if (err) throw err;
      console.log("1 record inserted");
    });
  });

  const register = async () => {
    try {
      createUserWithEmailAndPassword(auth, email, pssw);
    } catch(error){};
  };

  const login = async () => {
    try {
      const user = await signInWithEmailAndPassword(auth, email, pssw);
    } catch(error){};
  };

  const logout = async () => {
    await signOut(auth);
  };

  return (
    <div className="wrapper">
      <input placeholder="Ingresa tu e-mail" onChange={(event) => {
        setEmail(event.target.value);
      }}></input><br/><br/>
      <input type="password" placeholder="Ingresa una password" onChange={(event) => {
        setPssw(event.target.value);
      }}></input><br/><br/>
      <button onClick={register}>Register User</button>
      <button onClick={login}>Login User</button>
      <button onClick={logout}>Logout User</button>
      <span>{user?.email}</span>
      <span>{user?.uid}</span>
    </div>
  );
}

export default App;
